CREATE DEFINER = 'root'@'localhost'
PROCEDURE Proc_GetCurrentUserInfo (IN UserId char(36), IN CurrDate datetime)
COMMENT 'Store lấy dữ liệu người dùng hiện tại đăng nhập'
BEGIN
  SELECT
    u.user_id,
    u.user_name,
    u.password,
    u.full_name,
    u.email,
    u.phone_number,
    u.image,
    us.score,
    us.time,
